glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
uniform sampler2D texture;
varying vec2 v_uv0;
uniform ARGS{
    vec4 color0;
    vec4 edge_color0;
    float u_edge;
    float edge_blur;
};
vec4 drawCircle() {
    float edge = u_edge;
    float dis = 0.0;
    vec2 uv = v_uv0;
        if ( uv.x < edge )
        {
            if ( uv.y < edge )
            {
                dis = distance( uv, vec2(edge, edge) );
            }
            if ( uv.y > (1.0 - edge) )
            {
                dis = distance( uv, vec2(edge, (1.0 - edge)) );
            }
        }
        else if ( uv.x > (1.0 - edge) )
        {
            if ( uv.y < edge )
            {
                dis = distance( uv, vec2((1.0 - edge), edge ) );
            }
            if ( uv.y > (1.0 - edge) )
            {
                dis = distance( uv, vec2((1.0 - edge), (1.0 - edge) ) );
            }
        }
        vec4 color = color0 * texture2D(texture,uv);
        vec4 edge_color = color;
        float blur = edge_blur;
          edge_color = edge_color0  * texture2D(texture,uv);
        if(dis > 0.001)
        {
            float gap = edge * blur;
            if(dis <= edge - gap)
            {
                color = color;
            }
            else if(dis <= edge)
            {
                float t = smoothstep(0.,gap,edge-dis);
                color = vec4(edge_color.rgb,t * edge_color.a);
            }else{
                color = vec4(edge_color.rgb,0.);
            }
        }
        else
        {
          color =  color;
        }
    return  color;
}
    void main()
    {
      vec4 color = drawCircle();
      gl_FragColor = color;
    }

