glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
  uniform sampler2D texture;
  varying vec2 v_uv0;
  uniform properties {
      vec2 iResolution;
      vec2 mouse;
      vec2 radius;
  };
  void mainImage( out vec4 fragColor, in vec2 uv )
  {
      vec2 imouse = vec2(mouse.x, iResolution.y - mouse.y);
      vec4 tex = texture2D(texture, uv);
      vec2 d = uv*iResolution.xy -imouse.xy ;
      vec2 s = radius;
      float r = dot(d, d)/dot(s,s);
      vec4 finalColor =  tex * (1.08 - r);
      fragColor = vec4(finalColor.rgb,0.75);
  }
  void main()
  {
      mainImage(gl_FragColor, v_uv0.xy);
  }

